// TaskItem.jsx
import React from 'react';
import './TaskItem.css';

const TaskItem = ({ task, onDeleteTask, onEditTask, onToggleTask }) => {
    const { id, description, due_date, priority, completed } = task;

    return (
        <li style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
            <input
                type="checkbox"
                checked={completed}
                onChange={() => onToggleTask(id)}
                style={{ marginRight: '10px' }}
            />
            <span style={{ flexGrow: 1, textDecoration: completed ? 'line-through' : 'none' }}>
                {description}
                {due_date && ` (Due: ${new Date(due_date).toLocaleDateString()})`}
                {` | Priority: ${priority}`}
            </span>
            <button onClick={() => onEditTask(task)} style={{ marginRight: '5px' }}>Edit</button>
            <button onClick={() => onDeleteTask(id)}>Delete</button>
        </li>
    );
};

export default TaskItem;
