// TaskManager.jsx
import React, { useState, useEffect } from "react";
import axios from "axios";
import TaskList from "./TaskList";
import TaskForm from "./TaskForm";
import EditTaskForm from "./EditTaskForm";

const TaskManager = ({ selectedTitleId, taskLists, setTaskLists }) => {
    const [editTask, setEditTask] = useState(null);
    const [selectedList, setSelectedList] = useState({ tasks: [] });



    // Fetch tasks for the selected title
    useEffect(() => {
        const fetchTasks = async () => {
            if (!selectedTitleId) return; // If no title selected, do nothing
            try {
                const response = await axios.get(`http://localhost:5000/titles/${selectedTitleId}/tasks`);
                setSelectedList(response.data); // This should include tasks
                console.log("Fetched Tasks:", response.data); // Debugging line
            } catch (error) {
                console.error("Error fetching tasks:", error);
            }
        };
        fetchTasks();
    }, [selectedTitleId]);

    // Function to add a new task
const addTask = async (description, due_date, priority) => {
  if (!selectedTitleId) {
      console.error("No title selected!");
      return; // Exit the function if no title is selected
  }

  try {
      const response = await axios.post(
          `http://localhost:5000/titles/${selectedTitleId}/tasks`,
          { description, due_date, priority }
      );

      if (response.data && response.data.task) { // Check for response.data.task
          const newTask = response.data.task; // Extract the task object
          console.log("New Task Added:", newTask); // Debugging line

          // Update the selectedList state with the new task
          setSelectedList((prevList) => ({
              ...prevList,
              tasks: [...prevList.tasks, newTask], // Update the task list
          }));

          // Update the taskLists state with the new task
          setTaskLists((prevLists) =>
              prevLists.map((list) =>
                  list.id === selectedTitleId
                      ? { ...list, tasks: [...list.tasks, newTask] }
                      : list
              )
          );

          // Optional: Log the updated state for verification
          console.log("Selected List after Add:", {
              ...selectedList,
              tasks: [...selectedList.tasks, newTask],
          });
          console.log("Task Lists after Add:", taskLists.map((list) =>
              list.id === selectedTitleId
                  ? { ...list, tasks: [...list.tasks, newTask] }
                  : list
          ));
      } else {
          console.error("Invalid response structure:", response.data);
      }
  } catch (error) {
      console.error("Error adding task:", error);
  }
};

    // Function to update a task
    const updateTask = async (updatedTask) => {
        try {
            const response = await axios.put(
                `http://localhost:5000/tasks/${updatedTask.id}`,
                {
                    description: updatedTask.description,
                    due_date: updatedTask.due_date,
                    priority: updatedTask.priority,
                    completed: updatedTask.completed,
                }
            );
            console.log("Server response:", response.data);

            // Update the task in the local state
            setSelectedList((prevList) => ({
                ...prevList,
                tasks: prevList.tasks.map((task) =>
                    task.id === updatedTask.id ? response.data : task
                ),
            }));
            setEditTask(null);
        } catch (error) {
            console.error("Error updating task:", error);
        }
    };

    // Function to toggle task completion
    const toggleTask = async (id) => {
      try {
          const response = await axios.put(`http://localhost:5000/tasks/${id}/toggle`);

          if (response.data && response.data.task) {
              const updatedTask = response.data.task;
              console.log("Task Toggled:", updatedTask); // Debugging line

              // Update the task in the selectedList
              setSelectedList((prevList) => ({
                  ...prevList,
                  tasks: prevList.tasks ? prevList.tasks.map((task) =>
                      task.id === id ? updatedTask : task
                  ) : [], // Fallback to an empty array if tasks is undefined
              }));

              // Optionally, update the overall task lists
              setTaskLists((prevLists) =>
                  prevLists.map((list) =>
                      list.id === selectedTitleId
                          ? { 
                              ...list, 
                              tasks: list.tasks ? list.tasks.map((t) => (t.id === id ? updatedTask : t)) : [] 
                            }
                          : list
                  )
              );
          } else {
              console.error("Invalid response structure:", response.data);
          }

      } catch (error) {
          console.error("Error toggling task:", error);
      }
  };

    // Function to delete a task
    const deleteTask = async (taskId) => {
      if (!selectedTitleId) {
          console.error("No title selected!");
          return; // Exit if no title is selected
      }

      try {
          await axios.delete(`http://localhost:5000/titles/${selectedTitleId}/tasks/${taskId}`);

          // Update the local state after a successful delete
          setSelectedList((prevList) => ({
              ...prevList,
              tasks: prevList.tasks.filter((task) => task.id !== taskId), // Remove the deleted task
          }));

          // Update taskLists if needed
          setTaskLists((prevLists) =>
              prevLists.map((list) =>
                  list.id === selectedTitleId
                      ? { ...list, tasks: list.tasks.filter((task) => task.id !== taskId) } // Filter out the deleted task
                      : list
              )
          );

      } catch (error) {
          console.error("Error deleting task:", error);
      }
  };


    if (!selectedList) return <div>Select a title to manage tasks.</div>;

    return (
        <div className="task-manager">
            <h3>{selectedList.title}</h3>

            {!editTask && (
                <TaskForm 
                    selectedTitleId={selectedTitleId}
                    onAddTask={addTask} // Pass the addTask function
                />
            )}

            {editTask && (
                <EditTaskForm
                    task={editTask}
                    onSave={updateTask}
                    onCancel={() => setEditTask(null)}
                />
            )}

            <TaskList
                tasks={selectedList.tasks} // Ensure this is the updated tasks array
                onEdit={setEditTask}
                onDelete={deleteTask}
                onToggle={toggleTask}
            />
        </div>
    );
};

export default TaskManager;
