// TaskManager.jsx
import React, { useState, useEffect } from 'react';
import TaskList from './TaskList';
import TaskForm from './TaskForm';
import EditTaskForm from './EditTaskForm';
import axios from 'axios';

const TaskManager = ({ selectedTitleId, taskLists, setTaskLists }) => {
    const [editTask, setEditTask] = useState(null);

    // Derive selectedList from taskLists
    const selectedList = taskLists.find((list) => list.id === selectedTitleId);

    // Add Task Function
    const addTask = async (description) => {
        try {
            const response = await axios.post(
                `http://localhost:5000/titles/${selectedTitleId}/tasks`,
                { description }
            );

            console.log("Full Response Data:", response.data); // For debugging

            // Extract the new task from the response
            const newTask = response.data.task;

            if (newTask) {
                console.log("New Task Added:", newTask); // Verify newTask content

                // Update the taskLists in the state
                setTaskLists((prevLists) =>
                    prevLists.map((list) =>
                        list.id === selectedTitleId
                            ? { ...list, tasks: [...list.tasks, newTask] }
                            : list
                    )
                );

                // Optionally, log the updated task lists
                // Note: Due to state being asynchronous, consider using useEffect for logging
            } else {
                console.error("New task data is missing in response:", response.data);
            }

        } catch (error) {
            console.error("Error adding task:", error);
        }
    };

    // Update Task Function
    const updateTask = async (updatedTask) => {
        try {
            const response = await axios.put(
                `http://localhost:5000/tasks/${updatedTask.id}`,
                {
                    description: updatedTask.description,
                    due_date: updatedTask.due_date,
                    priority: updatedTask.priority,
                    completed: updatedTask.completed,
                }
            );

            console.log("Task Updated:", response.data); // For debugging

            const updatedTaskData = response.data.task;

            if (updatedTaskData) {
                // Update the taskLists in the state
                setTaskLists((prevLists) =>
                    prevLists.map((list) =>
                        list.id === selectedTitleId
                            ? {
                                  ...list,
                                  tasks: list.tasks.map((task) =>
                                      task.id === updatedTaskData.id ? updatedTaskData : task
                                  ),
                              }
                            : list
                    )
                );

                // Clear the editTask state
                setEditTask(null);
            } else {
                console.error("Updated task data is missing in response:", response.data);
            }

        } catch (error) {
            console.error("Error updating task:", error);
        }
    };

    // Toggle Task Completion Function
    const toggleTask = async (id) => {
        const task = selectedList.tasks.find((t) => t.id === id);
        if (!task) {
            console.error("Task not found:", id);
            return;
        }

        try {
            const response = await axios.put(`http://localhost:5000/tasks/${id}`, {
                completed: !task.completed, // Toggle the completed status
            });

            console.log("Task Toggled:", response.data); // For debugging

            const updatedTask = response.data.task;

            if (updatedTask) {
                // Update the taskLists in the state
                setTaskLists((prevLists) =>
                    prevLists.map((list) =>
                        list.id === selectedTitleId
                            ? {
                                  ...list,
                                  tasks: list.tasks.map((t) =>
                                      t.id === id ? updatedTask : t
                                  ),
                              }
                            : list
                    )
                );
            } else {
                console.error("Toggled task data is missing in response:", response.data);
            }

        } catch (error) {
            console.error("Error toggling task:", error);
        }
    };

    // Delete Task Function
    const deleteTask = async (taskId) => {
        try {
            await axios.delete(`http://localhost:5000/tasks/${taskId}`);

            console.log(`Task with ID ${taskId} deleted successfully.`); // For debugging

            // Update the taskLists in the state by removing the deleted task
            setTaskLists((prevLists) =>
                prevLists.map((list) =>
                    list.id === selectedTitleId
                        ? { 
                            ...list, 
                            tasks: list.tasks.filter((task) => task.id !== taskId) 
                          }
                        : list
                )
            );

            // If the deleted task was being edited, cancel editing
            if (editTask && editTask.id === taskId) {
                setEditTask(null);
            }

        } catch (error) {
            console.error("Error deleting task:", error);
        }
    };

    if (!selectedList) return <div>Select a title to manage tasks.</div>;

    return (
        <div>
            <h3 style={{ cursor: "pointer" }}>
                {selectedList.title || "Title not found"}
            </h3>

            {!editTask && selectedList && (
                <TaskForm 
                    selectedTitleId={selectedTitleId}
                    onAddTask={(description) => addTask(description)}
                />
            )}

            {editTask && (
                <EditTaskForm
                    task={editTask}
                    onSave={updateTask}
                    onCancel={() => setEditTask(null)}
                />
            )}

            <TaskList
                tasks={selectedList.tasks || []}
                onEdit={setEditTask}
                onDelete={deleteTask}
                onToggle={toggleTask}
            />
        </div>
    );
};

export default TaskManager;
